import React, { useEffect, useState } from "react";
import axios from "axios";

function App() {
    const [users, setUsers] = useState([]);
    const [newUser, setNewUser] = useState({ userid: "", password: "", username: "", email: "" });

    useEffect(() => {
        axios.get("http://localhost:8085/api/users")
            .then(response => {
                setUsers(response.data);
            })
            .catch(error => {
                console.error("Error fetching users:", error);
            });
    }, []);

    const handleChange = (e) => {
        setNewUser({ ...newUser, [e.target.name]: e.target.value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        axios.post("http://localhost:8085/api/users", newUser)
            .then(response => {
                alert("User added successfully!");
                setUsers([...users, response.data]);
                setNewUser({ userid: "", password: "", username: "", email: "" });
            })
            .catch(error => console.error("Error adding user:", error));
    };

    return (
        <div>
            <h1>Spring Boot + React 연동</h1>
            <form onSubmit={handleSubmit}>
                <input type="text" name="userid" placeholder="User ID" onChange={handleChange} value={newUser.userid} required />
                <input type="password" name="password" placeholder="Password" onChange={handleChange} value={newUser.password} required />
                <input type="text" name="username" placeholder="Name" onChange={handleChange} value={newUser.username} required />
                <input type="email" name="email" placeholder="Email" onChange={handleChange} value={newUser.email} required />
                <button type="submit">Add User</button>
            </form>

            <h2>User List</h2>
            <ul>
                {users.map(user => (
                    <li key={user.userid}>{user.username} - {user.email}</li>
                ))}
            </ul>
        </div>
    );
}

export default App;
