import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

function Dashboard() {
    const [userInfo, setUserInfo] = useState({
        username: "",
        authorities: [],
        authenticated: false,
    });

    const navigate = useNavigate();

    useEffect(() => {
        // ‚úÖ ÏÑ∏ÏÖò ÎòêÎäî Î°úÏª¨ Ïä§ÌÜ†Î¶¨ÏßÄÏóêÏÑú Î°úÍ∑∏Ïù∏ Ï†ïÎ≥¥ Í∞ÄÏ†∏Ïò§Í∏∞
        const storedUser = sessionStorage.getItem("user"); // localStorage.getItem("user") ÎèÑ Í∞ÄÎä•

        if (storedUser) {
            setUserInfo(JSON.parse(storedUser)); // ‚úÖ Ï†ÄÏû•Îêú Î°úÍ∑∏Ïù∏ Ï†ïÎ≥¥ ÏÇ¨Ïö©
        }
    }, [navigate]);

    const handleLogout = () => {
        axios.post("http://localhost:8085/logout", {}, { withCredentials: true })
            .then(() => {
                // ‚úÖ ÏÑ∏ÏÖò Ïä§ÌÜ†Î¶¨ÏßÄ Î∞è Î°úÏª¨ Ïä§ÌÜ†Î¶¨ÏßÄ ÏÇ≠Ï†ú
                sessionStorage.removeItem("user");
                localStorage.removeItem("user");
    
                // ‚úÖ ÏÉÅÌÉú Ï¥àÍ∏∞Ìôî
                setUserInfo({ username: "", authorities: [], authenticated: false });
    
                // ‚úÖ Î°úÍ∑∏Ïù∏ ÌéòÏù¥ÏßÄÎ°ú Ïù¥Îèô
                window.location.href = "/dashboard";
            })
            .catch(error => console.error("Logout failed:", error));
    };

    return (
        <div>
            <h1 style={{ color: "red" }}>ÎåÄÏâ¨Î≥¥Îìú ÌôîÎ©¥</h1>

            <h4>
                <p>üë§ Î°úÍ∑∏Ïù∏ ÏÇ¨Ïö©Ïûê : {userInfo.authenticated ? userInfo.username : "Î°úÍ∑∏Ïù∏ ÏïàÎê®"}</p>
                <p>üîë ÏÇ¨Ïö©Ïûê Í∂åÌïú : {userInfo.authenticated ? (userInfo.authorities?.join(", ") || "Í∂åÌïú ÏóÜÏùå") : "Í∂åÌïú ÏóÜÏùå"}</p>
            </h4>

            <div>
                {userInfo.authenticated ? (
                    <button onClick={handleLogout}>Î°úÍ∑∏ÏïÑÏõÉ</button>
                ) : (
                    <>
                        <a href="/login"><button>Î°úÍ∑∏Ïù∏</button></a>
                        <a href="/signup"><button>ÌöåÏõêÍ∞ÄÏûÖ</button></a>
                    </>
                )}
            </div>
        </div>
    );
}

export default Dashboard;
