import axios from "axios";
import { useState } from "react";
import { useNavigate } from "react-router-dom";

function Login() {
    const [credentials, setCredentials] = useState({ userid: "", password: "" });
    const [errorMessage, setErrorMessage] = useState("");
    const navigate = useNavigate();

    const handleChange = (e) => {
        setCredentials({ ...credentials, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post("http://localhost:8085/api/auth/login", credentials, {
                withCredentials: true, // ✅ 세션 유지 설정 추가
            });

            if (response.data.authenticated) {
                alert("로그인 성공!");
                navigate("/dashboard");
            }
        } catch (error) {
            setErrorMessage("로그인 실패! 아이디 또는 비밀번호를 확인하세요.");
        }
    };

    return (
        <div>
            <h2>로그인</h2>
            {errorMessage && <p style={{ color: "red" }}>{errorMessage}</p>}
            <form onSubmit={handleSubmit}>
                <input type="text" name="userid" placeholder="아이디" onChange={handleChange} required />
                <input type="password" name="password" placeholder="비밀번호" onChange={handleChange} required />
                <button type="submit">로그인</button>
            </form>
        </div>
    );
}

export default Login;
